mapper.select:
select * from meta_const_detail
where del_sign=0
and const_detail_id = #{constDetailId,jdbcType=INTEGER}

select count(*) from meta_const_detail
where del_sign=0
and const_detail_id = #{constDetailId,jdbcType=INTEGER}

select * from meta_const_detail
where del_sign=0
and const_id = #{constId,jdbcType=INTEGER}
