mapper.select:
select * from meta_const_detail
where delSign=0
and constDetailId = #{constDetailId,jdbcType=INTEGER}

select count(*) from meta_const_detail
where delSign=0
and constDetailId = #{constDetailId,jdbcType=INTEGER}

select * from meta_const_detail t
where t.delSign=0
and t.constId = #{constId,jdbcType=INTEGER}

select * from meta_const_detail
where delSign=0
and constId = #{constId,jdbcType=INTEGER}
