mapper.select:
select * from meta_const
where delSign=0
and constId = #{constId,jdbcType=INTEGER}

select count(*) from meta_const
where delSign=0
and constId = #{constId,jdbcType=INTEGER}

select count(*) from (
select * from meta_const t
where t.delSign=0
and t.projectId = #{projectId,jdbcType=INTEGER}
) as count_select_

select * from meta_const t
where t.delSign=0
and t.projectId = #{projectId,jdbcType=INTEGER}
order by t.createDate
limit #{startIndex},#{pageSize}

select constId from meta_const
where delSign=0
and projectId = #{projectId,jdbcType=INTEGER}
