mapper.select:
select * from meta_entity
where delSign=0
and entityId = #{entityId,jdbcType=INTEGER}

select count(*) from meta_entity
where delSign=0
and entityId = #{entityId,jdbcType=INTEGER}

select count(*) from (
select 1 from meta_entity t
where t.delSign=0
and t.projectId = #{projectId,jdbcType=INTEGER}
) as count_select_

select * from meta_entity t
where t.delSign=0
and t.projectId = #{projectId,jdbcType=INTEGER}
order by t.createDate
limit #{startIndex},#{pageSize}

select entityId from meta_entity
where delSign=0
and projectId = #{projectId,jdbcType=INTEGER}
