mapper.select:
select * from meta_index
where delSign=0
and indexId = #{indexId,jdbcType=INTEGER}

select count(*) from meta_index
where delSign=0
and indexId = #{indexId,jdbcType=INTEGER}

mapper.update:
update meta_index
set indexName = #{indexName,jdbcType=VARCHAR},
entityId = #{entityId,jdbcType=INTEGER},
`unique` = #{unique,jdbcType=SMALLINT},
operateDate = #{operateDate,jdbcType=TIMESTAMP},
operateBy = #{operateBy,jdbcType=VARCHAR},
version = #{version,jdbcType=INTEGER}
where indexId = #{indexId,jdbcType=INTEGER}
and version=#{version,jdbcType=INTEGER}
and delSign=0
